const PostImages = ({ images }) => {
  if (images.length === 1) {
    return (
      <>
        <img src={images[0].src} />
      </>
    );
  }
  if (images.length === 2) {
    return (
      <>
        <img src={images[0].src} />
        <img src={images[1].src} />
      </>
    );
  }
  return (
    <>
      <img src={images[0].src} />
      <div
        style={{
          display: "inline-block",
          width: "50%",
          textAlign: "center",
          verticalAlign: "middle",
        }}
      >
        <PlusOutlined />
        <br />
        {images.length - 1} 개의 사진 더보기
      </div>
    </>
  );
};

export default PostImages;
